/* Grundformatierung ********************************************/

*,
*:after,
*:before
{
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}

.clear:after
{
  content: ".";
  clear: both;
  display: block;
  visibility: hidden;
  height: 0;
}

html,
body
{
  height: 100%;
  min-width: 320px;
  margin: 0;
  padding: 0;
  color: #000;
  font-size: 14px;
}

